import{_ as t,c as e,o as l,d as n}from"./app.e8bbb56b.js";const c=JSON.parse('{"title":"拗音系列，长音","description":"","frontmatter":{},"headers":[{"level":2,"title":"拗音","slug":"拗音","link":"#拗音","children":[]},{"level":2,"title":"拗音读音需要注意点","slug":"拗音读音需要注意点","link":"#拗音读音需要注意点","children":[]},{"level":2,"title":"长音","slug":"长音","link":"#长音","children":[]},{"level":2,"title":"特点","slug":"特点","link":"#特点","children":[]},{"level":2,"title":"平假名长音","slug":"平假名长音","link":"#平假名长音","children":[]}],"relativePath":"japanese/おんせい/05.拗音系列，长音.md"}'),s={name:"japanese/おんせい/05.拗音系列，长音.md"},r=n('<h1 id="拗音系列-长音" tabindex="-1">拗音系列，长音 <a class="header-anchor" href="#拗音系列-长音" aria-hidden="true">#</a></h1><h2 id="拗音" tabindex="-1">拗音 <a class="header-anchor" href="#拗音" aria-hidden="true">#</a></h2><ol><li>组合：在い段假名（除い以外）的右下角加上小写的やゆよ其中的一个。</li><li>书写：一大一小，不分开。</li><li>罗马音：罗马音组合，都去掉一个 i</li></ol><table><thead><tr><th style="text-align:left;">拗音</th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>きゃ</strong>　キャ kya</td><td style="text-align:left;"><strong>きゅ</strong> キュ kyu</td><td style="text-align:left;"><strong>きょ</strong> キョ kyo</td></tr><tr><td style="text-align:left;"><strong>しゃ</strong> シャ sya</td><td style="text-align:left;"><strong>しゅ</strong> シュ syu</td><td style="text-align:left;"><strong>しょ</strong> ショ syo</td></tr><tr><td style="text-align:left;"><strong>ちゃ</strong> チャ tya</td><td style="text-align:left;"><strong>ちゅ</strong> チュ tyu</td><td style="text-align:left;"><strong>ちょ</strong> チョ tyo</td></tr><tr><td style="text-align:left;"><strong>にゃ</strong> ニャ nya</td><td style="text-align:left;"><strong>にゅ</strong> ニュ nyu</td><td style="text-align:left;"><strong>にょ</strong> ニョ nyo</td></tr><tr><td style="text-align:left;"><strong>ひゃ</strong> ヒャ hya</td><td style="text-align:left;"><strong>ひゅ</strong> ヒュ hyu</td><td style="text-align:left;"><strong>ひょ</strong> ヒョ hyo</td></tr><tr><td style="text-align:left;"><strong>みゃ</strong> ミャ mya</td><td style="text-align:left;"><strong>みゅ</strong> ミュ myu</td><td style="text-align:left;"><strong>みょ</strong> ミョ myo</td></tr><tr><td style="text-align:left;"><strong>りゃ</strong> リャ rya</td><td style="text-align:left;"><strong>りゅ</strong> リュ ryu</td><td style="text-align:left;"><strong>りょ</strong> リョ ryo</td></tr></tbody></table><table><thead><tr><th style="text-align:left;">拗音-浊音</th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>ぎゃ</strong>　ギャ gya</td><td style="text-align:left;"><strong>ぎゅ</strong> ギュ gyu</td><td style="text-align:left;"><strong>ぎょ</strong> ギョ gyo</td></tr><tr><td style="text-align:left;"><strong>じゃ</strong> ジャ zya</td><td style="text-align:left;"><strong>じゅ</strong> ジュ zyu</td><td style="text-align:left;"><strong>じょ</strong> ジョ zyo</td></tr><tr><td style="text-align:left;"><strong>ぢゃ</strong> ヂャ dya</td><td style="text-align:left;"><strong>ぢゅ</strong> ヂュ dyu</td><td style="text-align:left;"><strong>ぢょ</strong> ヂョ dyo</td></tr><tr><td style="text-align:left;"><strong>びゃ</strong> ビャ bya</td><td style="text-align:left;"><strong>びゅ</strong> ビュ byu</td><td style="text-align:left;"><strong>びょ</strong> ビョ byo</td></tr><tr><td style="text-align:left;"><strong>ぴゃ</strong> ピャ pya</td><td style="text-align:left;"><strong>ぴゅ</strong> ピュ pyu</td><td style="text-align:left;"><strong>ぴょ</strong> ピョ pyo</td></tr></tbody></table><h2 id="拗音读音需要注意点" tabindex="-1">拗音读音需要注意点 <a class="header-anchor" href="#拗音读音需要注意点" aria-hidden="true">#</a></h2><ol><li>じゃ(jia)、じゅ(jiu)、じょ(jio).</li><li>ぢゃ(jia)、ぢゅ(jiu)、ぢょ(jio).</li></ol><p>以上1跟2的发音是一样的，但是 罗马音输入的时候不能打错哟~</p><h2 id="长音" tabindex="-1">长音 <a class="header-anchor" href="#长音" aria-hidden="true">#</a></h2><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-hidden="true">#</a></h2><ol><li>发音规则：长音就是把假名拉长一拍念的音。，除拨音[h]和促音以外，日语的每个音节都可以发成长音。</li><li>片假名长音用ー来表示。</li></ol><h2 id="平假名长音" tabindex="-1">平假名长音 <a class="header-anchor" href="#平假名长音" aria-hidden="true">#</a></h2><ol><li>あ段假名后加あ，如：おかあさん o ka a sa nn.</li><li>い段假名后加い，如：おじいさん o ji i sa nn.</li><li>う段假名后加う，如：ゆうき yu u ki.</li><li>え段假名后加い，如：せんせい se nn se i.</li><li>お段假名后加う，如：おとうさん o to u sa nn.</li></ol><blockquote><p>少数特殊的单词（遇到了特殊记忆）： え段假名后加え，如：おねえさん o ne e sa nn お段假名后加お，如：おおきい o o ki i</p></blockquote>',14),a=[r];function o(d,i,g,y,h,f){return l(),e("div",null,a)}const _=t(s,[["render",o]]);export{c as __pageData,_ as default};
